    private void btnCargarModeloMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnCargarModeloMouseClicked
        int n=Integer.parseInt((String) cboCuadrado.getSelectedItem());
        modelo=new ModeloMatriz(n); 
        tblMatriz.setModel(modelo);
        modelo.cargarMatriz();
    }//GEN-LAST:event_btnCargarModeloMouseClicked

